package main

import (
	"encoding/hex"
	"fmt"
	"math/big"
	"testing"
)

func TestDecodeInt(t *testing.T) {
	var largePos1 big.Int
	(&largePos1).SetString("18446744073709551615", 10)

	var largePos2 big.Int
	(&largePos2).SetString("18446744073709551616", 10)

	var largeNeg1 big.Int
	(&largeNeg1).SetString("-18446744073709551616", 10)

	var largeNeg2 big.Int
	(&largeNeg2).SetString("-18446744073709551617n", 10)

	tests := []struct{
		value *big.Int
		cbor string
	}{
		{
			value: big.NewInt(0),
			cbor: "00",
		},
		{
			value: big.NewInt(1),
			cbor: "01",
		},
		{
			value: big.NewInt(10),
			cbor: "0a",
		},
		{
			value: big.NewInt(23),
			cbor: "17",
		},
		{
			value: big.NewInt(24),
			cbor: "1818",
		},
		{
			value: big.NewInt(25),
			cbor: "1819",
		},
		{
			value: big.NewInt(100),
			cbor: "1864",
		},
		{
			value: big.NewInt(1000),
			cbor: "1903e8",
		},
		{
			value: big.NewInt(1000000),
			cbor: "1a000f4240",
		},
		{
			value: big.NewInt(1000000000000),
			cbor: "1b000000e8d4a51000",
		},
		{
			value: &largePos1,
			cbor: "1bffffffffffffffff",
		},
		{
			value: &largePos2,
			cbor: "c249010000000000000000",
		},
		{
			value: &largeNeg1,
			cbor: "3bffffffffffffffff",
		},
		{
			value: &largeNeg2,
			cbor: "c349010000000000000000",
		},
		{
			value: big.NewInt(-1),
			cbor: "20",
		},
		{
			value: big.NewInt(-10),
			cbor: "29",
		},
		{
			value: big.NewInt(-100),
			cbor: "3863",
		},
		{
			value: big.NewInt(-1000),
			cbor: "3903e7",
		},
	}

	for _, tt := range tests {
		t.Run(fmt.Sprintf("int %d", tt.value), func(t *testing.T) {
			actual := encodeInt(tt.value)
			actualHex := hex.EncodeToString(actual)

			if actualHex != tt.cbor {
				t.Fatalf("invalid int encoding")
			}

			d, err := Decode(actual)
			if err != nil {
				t.Fatalf("bad actual cbor (%v)", err)
			}

			dInt, ok := d.(*DecodedInt)
			if !ok {
				t.Fatalf("didn't decode an int")
			}

			if dInt.Value.Cmp(tt.value) != 0 {
				t.Fatalf("roundtrip failed")
			}
		})
	}
}

func TestDecodeCbor(t *testing.T) {
	tests := []struct {
		name string
		cbor string
	}{
		{
			name: "basic UTXO",
			cbor: "",
		},
		{
			name: "basic tx",
			cbor: "",
		},
		{
			name: "basic block",
			cbor: "",
		},
		{
			name: "block with smart contracts",
			cbor: "",
		},
		{
			name: "another block",
			cbor: "",
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			bs, err := hex.DecodeString(tt.cbor)
			if err != nil {
				t.Fatalf("bad input %v", err)
			}

			d, err := Decode(bs)
			if err != nil {
				t.Fatalf("bad cbor %v", err)
			}

			// make sure the roundtrip is the same
			bs_ := d.Cbor()
			bsHex := hex.EncodeToString(bs_)

			if bsHex != tt.cbor {
				fmt.Println(bsHex)
				t.Fatalf("roundtrip failed")
			}
		})
	}
}